import React from 'react'
import { Container } from 'react-bootstrap'


const header = (props) => {

    const headerStyle = {
        backgroundColor: props.color,
        height: "250px"

    }

    return (
        <div style={headerStyle}>
            <Container>

                {props.text}
            </Container>
        </div>
    )
}

export default header
