import React from 'react'
import { Container } from 'react-bootstrap'


const header = (props) => {

    const headerStyle = {
        backgroundColor: props.color,
        height: "250px",
        display: "flex",
        justifyContent: "flex-end",

    }

    const titleStyle = {
        fontWeight: "bold",
        textAlign: "center"
    }

    return (
        <div style={headerStyle}>
            <Container>
                <h1 style={titleStyle}>{props.text}</h1>
            </Container>
        </div>
    )
}

export default header
